import axios from 'axios'
import React,{useState,useEffect} from 'react'
import { useNavigate ,useParams} from 'react-router-dom';
import styles from "./styles.module.css"

function Edit() {
  const [firstName,setfirstName] =useState('')
  const [lastName,setlastName] =useState('')
  const [email,setEmail] = useState('')
  const navigate = useNavigate()
  const {id}=useParams();
  console.log(id +"==> this is id.");

  useEffect(() => {
    const token = localStorage.getItem("Admintoken");
    if (!token) {
      navigate("/adminlogin8714");
      return false;
    }
    getDataById();
    
  }, [])
 

 async function getDataById(){
    const user= await axios.get('http://localhost:8080/api/getuser/'+id)
    setfirstName(user.data.resp.firstName);
    setlastName(user.data.resp.lastName);
    setEmail(user.data.resp.email);

  }
 
  async function updateuser(e){
     e.preventDefault();
     const newData={firstName,lastName,email,id}
     await  axios.post('http://localhost:8080/api/update',newData).then(()=>{
        setfirstName('');
        setlastName('');
        setEmail('');
        navigate('/usersList');
    })
    }


  

    return (
        <div>
 <body class="bg-white-500">
   <div class="container mx-auto p-2">
     <div class="max-w-sm mx-auto my-24 bg-white px-5 py-10 rounded shadow-xl">
       <div class="text-center mb-8">
         <h1 class="font-bold text-2xl">Edit User</h1>
       </div>
       <form action="#">
         <div class="mt-5">
           <label for="username">Username </label>
           <input value={firstName} onChange={(e)=>setfirstName(e.target.value)}
             type="text"
             id="username"
             class="block w-full p-2 border rounded border-gray-500"
           />
         </div>
         <div class="mt-5">
           <label for="email">Email</label>
           <input value={email} onChange={(e)=>setEmail(e.target.value)}
             type="email"
             id="email"
             class="block w-full p-2 border rounded border-gray-500"
           />
         </div>
         <div class="mt-10">
           <input onClick={updateuser}
             type="submit"
             value="Update"
             class="py-3 bg-green-500 hover:bg-green-600 rounded text-white text-center w-full"
           />
         </div>
       </form>
     </div>
   </div>
 </body>
 <div className={styles.signup_container}>
			<div className={styles.signup_form_container}>
				<div className={styles.left}>
					<h1>Welcome</h1>
					<h4 style={{color:"white"}}> Looks like you're new here! </h4>
					<Link to="/login">
						<button type="button" className={styles.white_btn}>
							Sign in
						</button>
					</Link>
				</div>
				<div className={styles.right}>
					<form className={styles.form_container} onSubmit={handleSubmit}>
						<h1>Create Account</h1>
						{error && <div className={styles.error_msg}>{error}</div>}
						<input
							type="text"
							placeholder=""
							name="firstName"
							value={firstName} onChange={(e)=>setfirstName(e.target.value)}
                            id="usname"
							required
							className={styles.input}
							autocomplete="off"
						/>
						<input
							type="text"
							placeholder="Last Name"
							name="lastName"
							onChange={handleChange}
							value={data.lastName}
							required
							className={styles.input}
							autocomplete="off"
						/>
						<input
							type="email"
							placeholder=""
							name="email"
                            id="email"
							value={email} 
                            onChange={(e)=>setEmail(e.target.value)}
							required
							className={styles.input}
							autocomplete="off"
						/>
						<input
							type="password"
							placeholder="Password"
							name="password"
							onChange={handleChange}
							value={data.password}
							required
							className={styles.input}
							autocomplete="off"
						/>
						
						<button onClick={updateuser} type="submit" className={styles.green_btn}>
							Update
						</button>
					</form>
				</div>
			</div>
		</div>

        </div>
    )
}

export default Edit
