import axios from "axios";
import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { Table, TableHead, TableCell, Paper, TableRow, TableBody, Button, makeStyles } from '@material-ui/core'
const useStyles = makeStyles({
    table: {
        width: '90%',
        margin: '50px 0 0 50px'
    },
    thead: {
        '& > *': {
            fontSize: 20,
            background: '#000000',
            color: '#F7A200'
        }
    },
    row: {
        '& > *': {
            fontSize: 18
        }
    }
})


function UsersList() {
    const [users, setUsers] = useState([]);
    const navigate = useNavigate();
    const classes = useStyles();

    useEffect(() => {
        const token = localStorage.getItem("Admintoken");
        if (!token) {
            navigate("/adminlogin8714");
        }
        getUserData();
    }, []);

    const getUserData = async () => {
        const result = await axios.get("http://localhost:8080/api/fetchuserdata");
        console.log("Fetching User Data.........");
        console.log("Done");
        setUsers(result.data.resp);
    };
    const deleteUser = async (userId) => {
        console.log(userId);
        if (window.confirm("Are you sure ?")) {
            console.log(userId);
            const newData = await axios.post("http://localhost:8080/api/delete/" + userId);
            if (newData) {
                setUsers(users.filter((user) => user._id != userId));
            }else{console.log("Not Working");}
        }
    };

    return (


        <Table className={classes.table}>
            <TableHead>
                <TableRow className={classes.thead}>
                    <TableCell>No:</TableCell>
                    <TableCell>First Name</TableCell>
                    <TableCell>Last Name</TableCell>
                    <TableCell>Email</TableCell>
                    <TableCell></TableCell>
                </TableRow>
            </TableHead>
            <TableBody>
                {users.map((user, index) => (
                    <TableRow className={classes.row} key={user.id}>
                        <TableCell>{++index}</TableCell> {/* change it to user.id to use JSON Server */}
                        <TableCell>{user.firstName}</TableCell>
                        <TableCell>{user.lastName}</TableCell>
                        <TableCell>{user.email}</TableCell>
                        
                        <TableCell>
                            <Button color="primary" variant="contained" style={{ marginRight: 10 }} component={Link} to={`/edituser/${user._id}`}>Edit</Button> {/* change it to user.id to use JSON Server */}
                            <Button color="secondary" variant="contained" onClick={(us) => {deleteUser(user._id); }}>Delete</Button> {/* change it to user.id to use JSON Server */}
                        </TableCell>
                    </TableRow>
                ))}
            </TableBody>
        </Table>
     )
    }

    
export default UsersList;
